import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
import _ from "lodash";
import Creatable from "react-select";
import { components } from "react-select";

const { Option } = Select;
const customStyles = {
  option: (provided, state) => ({
    ...provided,
    color: state.isSelected ? "red" : "black",
    padding: 20,
  }),
};

const Menu = (props) => {
  const optionSelectedLength = props.getValue().length || 0;
  console.log(props, "props");
  return (
    <components.Menu {...props} style={{ color: "black" }}>
      {optionSelectedLength < 5 ? (
        props.children
      ) : (
        <div style={{ margin: 15 }}>Max limit achieved</div>
      )}
    </components.Menu>
  );
};

class step3 extends Component {
  state = {
    data: [],
    ans: [],
    isMax: false,
    blocked: [],
  };
  componentDidMount() {
    fetch("http://www.mocky.io/v2/5eaa841f2d000095002686ba")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          let newData = [];
          result.Data.map((item) => {
            newData.push({
              label: item,
              value: item,
            });
          });
          this.setState({ data: newData });
          // this.setState({ notBlocked: result.Data });
        },
        (error) => {}
      );
  }

  render() {
    const { data, blocked } = this.state;
    if (this.props.currentStep !== 4) {
      return null;
    }
    const isValidNewOption = (inputValue, selectValue) =>
      inputValue.length > 0 && selectValue.length < 5;
    return (
      <div>
        <label>
          Please select your sector of interest to invest in: Select up to 5
          sectors
        </label>

        <Creatable
          styles={customStyles}
          components={{ Menu }}
          isMulti
          isValidNewOption={isValidNewOption}
          options={data}
          theme={(theme) => ({
            ...theme,
            borderRadius: 0,
            colors: {
              ...theme.colors,
              primary25: "hotpink",
              primary: "black",
            },
          })}
        />

        {/* <Select
          filterOption="3"
          mode="multiple"
          style={{ width: "100%" }}
          onChange={(event) => this.checkMax(event)}
        >
          {data.map((item) => {
            return (
              <Option
                key={item}
                value={item}
                disabled={blocked.includes(item) ? true : false}
              >
                {item}
              </Option>
            );
          })}
        </Select> */}
      </div>
    );
  }
}

export default step3;
