import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
import { Checkbox, Row, Col } from "antd";
import _ from "lodash";
const { Option } = Select;
class step5 extends Component {
  state = {
    data: [],
    ans: [],
    isMax: false,
    checked: [],
  };
  componentDidMount() {}

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  onChange = (checkedValues) => {
    this.setState(() => {
      return { checked: checkedValues };
    });
    this.props.handleChange(checkedValues, 8);
  };

  isDisabled = (id) => {
    console.log(id);
    return (
      this.state.checked.length > 1 && this.state.checked.indexOf(id) === -1
    );
  };

  render() {
    const { data } = this.props;
    const { checked } = this.state;
    if (this.props.currentStep !== 5) {
      return null;
    }

    // console.log(data, "page 6");
    // Object.keys(data).map(function (s) {
    //   console.log(data[s]);
    // });
    // console.log(this.state.checked, "checkled");
    return (
      <div>
        <label>
          Please select your preference of stocks to invest in: (Select up to 4
          stocks in total from the displayed choices)
        </label>
        <Checkbox.Group style={{ width: "100%" }} onChange={this.onChange}>
          {!_.isEmpty(data)
            ? Object.keys(data).map(function (key) {
                return (
                  <div>
                    <label>{key}</label>
                    <Row>
                      {data[key].map((item) => {
                        return (
                          <Col span={8}>
                            <Checkbox
                              value={item[1]}
                              disabled={
                                checked.length > 3 &&
                                checked.indexOf(item[1]) === -1
                              }
                            >
                              {item[0]}
                            </Checkbox>
                          </Col>
                        );
                      })}
                    </Row>
                  </div>
                );
              })
            : null}
        </Checkbox.Group>
        <Modal
          title="Basic Modal"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
          <p>Some contents...</p>
          <p>Some contents...</p>
          <p>Some contents...</p>
        </Modal>
      </div>
    );
  }
}

export default step5;
