import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
const { Option } = Select;
class step3 extends Component {
  state = {
    data: [],
    ans: [],
    isMax: false,
    notBlocked: [],
  };
  componentDidMount() {
    fetch("http://www.mocky.io/v2/5eaa841f2d000095002686ba")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          this.setState({ data: result.Data });
        },
        (error) => {}
      );
  }

  checkMax(event) {
    let tempData = this.state.data;
    if (event.length == 5) {
      let notBlocked = [];
      event.map((item) => {
        tempData.map((item2) => {
          if (item == item2) {
            notBlocked.push(item);
          }
        });
      });
      this.setState({ notBlocked });
    } else {
      this.props.handleChange(event, 7);
    }

    // if (this.state.ans.length >= 5) {
    //   this.state.isMax = true;
    //   return;
    // } else {
    //   let ans = this.state.ans;
    //   ans.push(event);
    //   this.state.ans = ans;
    //   this.state.isMax = false;
    //   this.props.handleChange(event, 7);
    // }
  }

  render() {
    const { data, notBlocked } = this.state;
    if (this.props.currentStep !== 4) {
      return null;
    }
    return (
      <div>
        <label>
          Please select your sector of interest to invest in: Select up to 5
          sectors
        </label>
        <Select
          filterOption="3"
          mode="multiple"
          style={{ width: "100%" }}
          onChange={(event) => this.checkMax(event)}
        >
          {data.map((item) => {
            return (
              <Option
                key={item}
                value={item}
                disabled={_.filter(notBlocked, (d) => {
                  d == item;
                })}
              >
                {item}
              </Option>
            );
          })}
        </Select>
      </div>
    );
  }
}

export default step3;
