import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
import _ from "lodash";
import Creatable from "react-select/lib/Creatable";
import { components } from "react-select";

const { Option } = Select;
const Menu = (props) => {
  const optionSelectedLength = props.getValue().length || 0;
  return (
    <components.Menu {...props}>
      {optionSelectedLength < 5 ? (
        props.children
      ) : (
        <div style={{ margin: 15 }}>Max limit achieved</div>
      )}
    </components.Menu>
  );
};

class step3 extends Component {
  state = {
    data: [],
    ans: [],
    isMax: false,
    blocked: [],
  };
  componentDidMount() {
    fetch("http://www.mocky.io/v2/5eaa841f2d000095002686ba")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          this.setState({ data: result.Data });
          // this.setState({ notBlocked: result.Data });
        },
        (error) => {}
      );
  }

  checkMax(event) {
    let tempData = this.state.blocked;
    if (event.length == 5) {
      tempData = event;
      this.setState({ blocked: tempData });
      this.props.handleChange(event, 7);
      let data = this.state.data;
      let newData = [];
      data.map((item) => {
        if (!tempData.includes(item)) {
          newData.push(item);
        }
      });
      console.log(newData, "blocked");
      this.setState({ blocked: newData });
    } else {
      tempData = event;
      this.setState({ blocked: tempData });
      this.props.handleChange(event, 7);
    }

    // if (this.state.ans.length >= 5) {
    //   this.state.isMax = true;
    //   return;
    // } else {
    //   let ans = this.state.ans;
    //   ans.push(event);
    //   this.state.ans = ans;
    //   this.state.isMax = false;
    //   this.props.handleChange(event, 7);
    // }
  }

  render() {
    const { data, blocked } = this.state;
    if (this.props.currentStep !== 4) {
      return null;
    }
    const isValidNewOption = (inputValue, selectValue) =>
      inputValue.length > 0 && selectValue.length < 5;
    return (
      <div>
        <label>
          Please select your sector of interest to invest in: Select up to 5
          sectors
        </label>

        <Creatable
          components={{ Menu }}
          isMulti
          isValidNewOption={isValidNewOption}
          options={data}
        />

        {/* <Select
          filterOption="3"
          mode="multiple"
          style={{ width: "100%" }}
          onChange={(event) => this.checkMax(event)}
        >
          {data.map((item) => {
            return (
              <Option
                key={item}
                value={item}
                disabled={blocked.includes(item) ? true : false}
              >
                {item}
              </Option>
            );
          })}
        </Select> */}
      </div>
    );
  }
}

export default step3;
