import React from "react";
import { useForm } from "react-hook-form";
import { Component } from "react";
import Radiochecklist from "./RadioChecklist"; // importing child component
import Step1 from "./BasicInfo";
import Step2 from "./RiskTolerance1";
import Step3 from "./RiskTolerance2";
import Step4 from "./page5";
import Step5 from "./page6";
class MasterForm extends Component {
  constructor(props) {
    super(props);
    // Set the initial input values
    this.state = {
      currentStep: 1, // Default is Step 1
      basicInfoAns: [],
      answer: [],
      page5_data: [],
      page6_data: {},
    };
    this.handleChange = this.handleChange.bind(this);
    this._next = this._next.bind(this);
    this._prev = this._prev.bind(this);
  }

  // handleChange = event => {
  //   // const { name, value } = event.target;
  //   // this.setState({
  //   //   [name]: value
  //   // });
  // };

  handlebasicInfoPage(event, question) {
    console.log(event, "event");
    console.log(`Question ${question}: answer is -> ${JSON.stringify(event)}`);
    let answer = this.state.basicInfoAns;
    answer[question] = event;
    this.state.basicInfoAns = answer;
  }

  handleChange(event, question) {
    console.log(`Question ${question}: answer is -> ${JSON.stringify(event)}`);
    let answer = this.state.answer;
    answer[question] = event;
    this.state.answer = answer;
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const { email, username, password } = this.state;
    alert(`Your registration detail: \n 
           Email: ${email} \n 
           Username: ${username} \n
           Password: ${password}`);
  };

  _next = () => {
    console.log(this.state.basicInfoAns, "basicInfo");
    console.log(this.state.answer, "answer");
    let currentStep = this.state.currentStep;
    // If the current step is 1 or 2, then add one on "next" button click
    currentStep = currentStep >= 3 ? 4 : currentStep + 1;
    if (currentStep == 4) {
      fetch("http://www.mocky.io/v2/5eaa841f2d000095002686ba")
        .then((res) => res.json())
        .then(
          (result) => {
            console.log(result);
            this.setState({ page5_data: result.Data });
          },
          (error) => {}
        );
    }
    this.setState({
      currentStep: currentStep,
    });
  };

  _prev = () => {
    let currentStep = this.state.currentStep;
    // If the current step is 2 or 3, then subtract one on "previous" button click
    currentStep = currentStep <= 1 ? 1 : currentStep - 1;
    this.setState({
      currentStep: currentStep,
    });
  };

  _submit = () => {
    let currentStep = this.state.currentStep;
    console.log(this.state.answer, "answer");
    console.log(this.state.basicInfoAns);
    //params
    let param = {
      Data: this.state.answer[7],
    };

    console.log(param, "page 5 param");
    //call api
    fetch("http://www.mocky.io/v2/5eae561d2f0000500019892e")
      // .then((res) => res.json())
      .then(
        (result) => {
          console.log(result, "mike");
          this.setState({ page6_data: result.Data });
        },
        (error) => {}
      );
    // next step
    currentStep = currentStep = currentStep + 1;
    this.setState({
      currentStep: currentStep,
    });
  };

  _submit2 = () => {
    let currentStep = this.state.currentStep;
    console.log(this.state.answer, "answer");
    console.log(this.state.basicInfoAns);
    //params
    let param = {
      Data: this.state.answer[7],
    };

    //call api
    fetch("http://www.mocky.io/v2/5eae561d2f0000500019892e")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          this.setState({ page6_data: result.Data });
        },
        (error) => {}
      );
    // next step
    currentStep = currentStep = currentStep + 1;
    this.setState({
      currentStep: currentStep,
    });
  };

  //button functions

  previousButton() {
    let currentStep = this.state.currentStep;
    if (currentStep !== 1) {
      return (
        <button
          className="btn btn-secondary"
          type="button"
          onClick={this._prev}
        >
          Previous
        </button>
      );
    }
    return null;
  }
  nextButton() {
    let currentStep = this.state.currentStep;
    if (currentStep < 4) {
      return (
        <button
          className="btn btn-primary float-right"
          type="button"
          onClick={this._next}
        >
          Next
        </button>
      );
    }
    return null;
  }

  submitButton() {
    let currentStep = this.state.currentStep;
    if (currentStep === 4 || currentStep === 5) {
      return (
        <button
          className="btn btn-success float-right"
          type="button"
          onClick={currentStep === 4 ? this._submit : this._submit2}
        >
          Submit
        </button>
      );
    }
    return null;
  }
  render() {
    var style = {
      display: "flex",
      justifyContent: "space-between",
      width: "100%",
      padding: "50px 0",
    };

    let textStyle = {
      textAlign: "center",
      paddingTop: "30px",
    };
    return (
      <React.Fragment>
        <h1>Portfolio Optimiser</h1>

        <h2>
          Investing is an effective way to put your money to work and build
          wealth. Kickstart your investment planning with us! Just fill up a
          short questionaire below
        </h2>

        <h3 style={textStyle}>Step {this.state.currentStep} </h3>

        <form onSubmit={this.handleSubmit}>
          <Step1
            currentStep={this.state.currentStep}
            handleChange={(event, qn) => this.handlebasicInfoPage(event, qn)}
          />
          <Step2
            currentStep={this.state.currentStep}
            handleChange={(event, qn) => this.handleChange(event, qn)}
            checkboxvalue={this.state.checkboxvalue}
            username={this.state.username}
          />
          <Step3
            currentStep={this.state.currentStep}
            handleChange={this.handleChange}
            password={this.state.password}
          />
          <Step4
            currentStep={this.state.currentStep}
            handleChange={this.handleChange}
            data={this.state.page5_data}
          />
          <Step5
            currentStep={this.state.currentStep}
            handleChange={this.handleChange}
            data={this.state.page6_data}
          />
          <div style={style}>
            {this.previousButton()}
            {this.nextButton()}
            {this.submitButton()}
          </div>
        </form>
      </React.Fragment>
    );
  }
}

export default MasterForm;
