import React from "react";
import { useForm } from "react-hook-form";
import { Component } from "react";
import { Form, Input, Button, Checkbox, Select } from "antd";
import { UserOutlined, DesktopOutlined } from "@ant-design/icons";
const { Option } = Select;
class step1 extends Component {
  state = {
    aboveAge: true,
  };
  checkAge(e) {
    console.log(e.target.value, "age");
    if (e.target.value < 18) {
      this.setState({ aboveAge: false });
    } else {
      this.setState({ aboveAge: true });
      this.props.handleChange(e, 1);
    }
  }
  render() {
    if (this.props.currentStep !== 1) {
      // Prop: The current step
      return null;
    }
    // The markup for the Step 1 UI
    return (
      <div>
        <h2>
          Welcome to CPF Investment Advisor (CIA) Here we hope to aid you in
          your stock investments :)
        </h2>
        <br />
        <label>Name</label>
        <Input
          size="large"
          placeholder="Name"
          onChange={(event) => this.props.handleChange(event, 0)}
        />
        <br />
        <label>Age</label>
        <Input
          size="large"
          placeholder="Age"
          onChange={(event) => this.checkAge(event)}
        />
        <div>
          style={{ color: "red" }}
          {!this.state.aboveAge
            ? "Please come back when you grow a pair, kthx bye"
            : ""}
        </div>

        <label>Are you an undischarged bankrupt?</label>
        <Select
          style={{ width: "100%" }}
          onChange={(event) => this.props.handleChange(event, 2)}
        >
          <Option key="yes" value="yes">
            Yes
          </Option>
          <Option key="no" value="no">
            No
          </Option>
          })}
        </Select>

        <br />
        <label>Amount in CPF OA:</label>
        <Input
          size="large"
          placeholder="CPF OA"
          onChange={(event) => this.props.handleChange(event, 3)}
        />

        <br />
        <label>Amount in CPF SA:</label>
        <Input
          size="large"
          placeholder="CPF SA"
          onChange={(event) => this.props.handleChange(event, 4)}
        />
      </div>

      // <div className="form-group">
      //   <label htmlFor="Name">Name</label>
      //   <input
      //     name="Name"
      //     placeholder="Enter Your Name"
      //     id="name"
      //     value={this.props.name}
      //     onChange={this.props.handleChange}
      //   />

      //   <label htmlFor="email">Email address</label>
      //   <input
      //     className="form-control"
      //     id="email"
      //     name="email"
      //     type="text"
      //     placeholder="Enter email"
      //     value={this.props.email} // Prop: The email input data
      //     onChange={this.props.handleChange} // Prop: Puts data into state
      //   />
      // </div>
    );
  }
}
export default step1;
