import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
const { Option } = Select;

class step2 extends Component {
  state = {
    checkboxvalue: 1,
    checkboxvalue2: 1,
    data: [
      {
        question:
          "1. I plan to begin withdrawing my money from my investments in:",
        default: "0",
        options: [
          {
            key: "0",
            value: "Less than 3 years",
          },
          {
            key: "1",
            value: "3 - 5 years",
          },
          {
            key: "2",
            value: "6 - 10 years",
          },
          {
            key: "3",
            value: "11 years",
          },
        ],
      },
    ],
  };

  handleChange(value, question) {
    console.log(`selected ${JSON.stringify(question)} answer is -> ${value}`);
  }

  onChange = (e) => {
    console.log("radio checked", e.target.value);
    this.setState({
      checkboxvalue: e.target.value,
      checkboxvalue2: e.target.value,
    });
  };

  render() {
    const { data } = this.state;
    if (this.props.currentStep !== 2) {
      // Prop: The current step
      return null;
    }

    return (
      <div>
        {/* 
        <select name="withdrawalTime">
          <option value="0">Less than 3 years</option>
          <option value="1">3 - 5 years</option>
          <option value="2">6 - 10 years</option>
          <option value="3">11 years</option>
        </select> */}
        <label>{data[0].question}</label>
        <Select
          defaultValue="0"
          style={{ width: 200 }}
          onChange={(value, event) => this.handleChange(value, event)}
        >
          <Option value="0">Less than 3 years</Option>
          <Option value="1">3 - 5 years</Option>
          <Option value="2">Lucy</Option>
          <Option value="3">Lucy</Option>
        </Select>

        <label>
          2. Once I begin withdrawing my funds, I plan to spend all of the funds
          in
        </label>
        <select name="withdrawalTime">
          <option value="0">Less than 3 years</option>
          <option value="1">3 - 5 years</option>
          <option value="2">6 - 10 years</option>
          <option value="3">11 years</option>
        </select>

        <label>3. I would describe my knowledge of investments as:</label>
        {/* <Radio.Group name ="investmentKnowledge" onChange={this.onChange} value={this.state.checkboxvalue}> */}
        <Radio.Group name="investmentKnowledge" defaultValue={1}>
          <Radio value={1}>None</Radio>
          <Radio value={2}>Limited</Radio>
          <Radio value={3}>Good</Radio>
          <Radio value={4}>Extensive</Radio>
        </Radio.Group>

        <label>4. When I invest my money, I am:</label>
        <Radio.Group name="investmentChoices" defaultValue={1}>
          <Radio value={1}>
            Most concerned about my investment losing value
          </Radio>
          <Radio value={2}>
            Equally concerned about my investment losing or gaining value
          </Radio>
          <Radio value={3}>
            Most concerned about my investment gaining value
          </Radio>
        </Radio.Group>
      </div>
    );
  }
}
export default step2;
