import React, { Component } from "react";
import { Radio, Input } from "antd";

class step3 extends Component {
    state = {
        checkboxvalue: 1,
      };
      onChange = e => {
        console.log("radio checked", e.target.value);
        this.setState({
          checkboxvalue: e.target.value
        });
      };
  render() {
    if (this.props.currentStep !== 3) {
      return null;
    }
    return (
      <div className="form-group">
        <label>5. Select the investments you currently own</label>
        <Radio.Group name="investmentOwned" defaultValue = {1}>
          <Radio value={1}>Bonds and/or bond funds</Radio>
          <Radio value={2}>Stocks and/or stock funds</Radio>
          <Radio value={3}>International securities and/ or international funds</Radio>
        </Radio.Group>

        <label>
          6. Consider this scenario: Imagine that in the past three months, the
          overall stock market lost 25% of its value. An individual stock
          investment you own also lost 25% of its value. What would you do?
        </label>
        <Radio.Group name="investmentChoice" defaultValue = {1}>
          <Radio value={1}>Sell all of my shares</Radio>
          <Radio value={2}>Sell some of my shares</Radio>
          <Radio value={3}>Do nothing</Radio>
          <Radio value={4}>Buy more shares</Radio>
        </Radio.Group>

        <button className="btn btn-success btn-block" input type="submit">
          Submit
        </button>
      </div>
    );
  }
}

export default step3;
