import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
import { Checkbox, Row, Col } from "antd";
import _ from "lodash";

import { Table, Tag } from "antd";
const { Option } = Select;
const columns = [
  {
    title: "No.",
    dataIndex: "index",
    key: "index",
  },
  {
    title: "Name",
    dataIndex: "name",
    key: "name",
  },
  {
    title: "Price",
    dataIndex: "price",
    key: "price",
  },
  {
    render: (text, record) => (
      <Space size="middle">
        <a>Invite {record.name}</a>
        <a>Delete</a>
      </Space>
    ),
  },

];

class step5 extends Component {
  state = {
    tableData: [],
    ans: [],
    isMax: false,
    checked: [],
  };
  componentDidMount() {}

  render() {
    const { data } = this.props;
    const { tableData } = this.state;
    if (this.props.currentStep !== 6) {
      return null;
    }
    let newData = [];
    // console.log(data, "page 7 data");
    if (!_.isEmpty(data) && _.isEmpty(tableData)) {
      let count = 1;
      data.stock_allocation.map((item) => {
        newData.push({
          index: count++,
          name: item[0],
          price: item[1],
        });
      });
      this.setState({ tableData: newData });
    }

    return (
      <div>
        <label>
          Based on your inputs, your risk profile is: <b>{data.risk_profile}</b>
          You will have to keep a minimum of $20,000 in your CPF OA account, taking that into account, maximum investible account will is
        </label>
        {/* <label>You have selected your preferred stocks of:</label> */}
        <label>
          We recommend you to invest a total of as an investment portfolio
          outlined below:
        </label>
        <Table columns={columns} dataSource={tableData} />
        <label>
          The expected return of this portfolio is <b>{data.best_return}%</b>.
          The expected risk is within your risk profile.
          "The expected return of this portfolio is <b>{data.best_return}</b>, with a risk of <text>{data.optimal_risk}</text>, which is well within your risk appetite of data.rules_risk that is calculated according to your risk profile."
        </label>
      </div>
    );
  }
}

export default step5;
