import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
import { Checkbox, Row, Col } from "antd";
import _ from "lodash";

import { Table, Tag } from "antd";
const { Option } = Select;
const columns = [
  {
    title: "No.",
    dataIndex: "index",
    key: "index",
  },
  {
    title: "Name",
    dataIndex: "name",
    key: "name",
  },
  {
    title: "Price",
    dataIndex: "price",
    key: "price",
  },
];

class step5 extends Component {
  state = {
    tableData: [],
    ans: [],
    isMax: false,
    checked: [],
  };
  componentDidMount() {}

  render() {
    const { data } = this.props;
    const { tableData } = this.state;
    if (this.props.currentStep !== 6) {
      return null;
    }
    let newData = [];
    console.log(data, "page 7 data");
    if (!_.isEmpty(data) && _.isEmpty(tableData)) {
      let count = 1;
      data.stock_allocation.map((item) => {
        newData.push({
          index: count++,
          name: item[0],
          price: item[1],
        });
      });
      this.setState({ tableData: newData });
    }

    return (
      <div>
        <label>
          Based on your inputs, your risk profile is: {data.risk_profile}
        </label>
        {/* <label>You have selected your preferred stocks of:</label> */}
        <label>
          We recommend you to invest a total of as an investment portfolio
          outlined below:
        </label>
        <Table columns={columns} dataSource={tableData} />
        <label>
          The expected return of this portfolio is {data.best_return}%. The
          expected risk is within your risk profile.
        </label>
      </div>
    );
  }
}

export default step5;
