import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
const { Option } = Select;

class step2 extends Component {
  state = {
    checkboxvalue: 1,
    checkboxvalue2: 1,
    answer: [],
    data: [
      {
        question:
          "1. I plan to begin withdrawing my money from my investments in:",
        default: "1",
        options: [
          {
            key: "1",
            value: "Less than 3 years",
          },
          {
            key: "2",
            value: "3 - 5 years",
          },
          {
            key: "3",
            value: "6 - 10 years",
          },
          {
            key: "4",
            value: "11 years",
          },
        ],
      },
      {
        question:
          "2. Once I begin withdrawing my funds, I plan to spend all of the funds in:",
        default: "1",
        options: [
          {
            key: "1",
            value: "Less than 3 years",
          },
          {
            key: "2",
            value: "3 - 5 years",
          },
          {
            key: "3",
            value: "6 - 10 years",
          },
          {
            key: "4",
            value: "11 years",
          },
        ],
      },
      {
        question: "3. I would describe my knowledge of investments as:",
        default: "1",
        options: [
          {
            key: "1",
            value: "None",
          },
          {
            key: "2",
            value: "Limited",
          },
          {
            key: "3",
            value: "Good",
          },
          {
            key: "4",
            value: "Extensive",
          },
        ],
      },
    ],
  };

  handleChange(event, question) {
    console.log(`Question ${question}: answer is -> ${event.value}`);
    let answer = this.state.answer;
    answer[question] = event.value;
    this.state.answer = answer;
  }

  onChange = (e) => {
    console.log("radio checked", e.target.value);
    this.setState({
      checkboxvalue: e.target.value,
      checkboxvalue2: e.target.value,
    });
  };

  render() {
    const { data } = this.state;
    if (this.props.currentStep !== 2) {
      // Prop: The current step
      return null;
    }

    return (
      <div>
        <label>{data[0].question}</label>
        <Select
          defaultValue="0"
          style={{ width: 200 }}
          onChange={(event) => this.handleChange(event, 0)}
        >
          {data[0].options.map((item) => {
            return (
              <Option key={item.key} value={item.key}>
                {item.value}
              </Option>
            );
          })}
        </Select>

        <label>{data[1].question}</label>
        <Select
          defaultValue="0"
          style={{ width: 200 }}
          onChange={(event) => this.handleChange(event, 1)}
        >
          {data[1].options.map((item) => {
            return (
              <Option key={item.key} value={item.key}>
                {item.value}
              </Option>
            );
          })}
        </Select>

        <label>3. I would describe my knowledge of investments as:</label>
        {/* <Radio.Group name ="investmentKnowledge" onChange={this.onChange} value={this.state.checkboxvalue}> */}
        <Radio.Group name="investmentKnowledge" defaultValue={1}>
          <Radio value={1}>None</Radio>
          <Radio value={2}>Limited</Radio>
          <Radio value={3}>Good</Radio>
          <Radio value={4}>Extensive</Radio>
        </Radio.Group>

        <label>4. When I invest my money, I am:</label>
        <Radio.Group name="investmentChoices" defaultValue={1}>
          <Radio value={1}>
            Most concerned about my investment losing value
          </Radio>
          <Radio value={2}>
            Equally concerned about my investment losing or gaining value
          </Radio>
          <Radio value={3}>
            Most concerned about my investment gaining value
          </Radio>
        </Radio.Group>
      </div>
    );
  }
}
export default step2;
