import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
import { Checkbox, Row, Col } from "antd";
const { Option } = Select;
class step5 extends Component {
  state = {
    data: [],
    ans: [],
    isMax: false,
    checked: [],
  };
  componentDidMount() {}

  onChange = (checkedValues) => {
    this.setState(() => {
      return { checked: checkedValues };
    });
  };

  isDisabled = (id) => {
    return (
      this.state.checked.length > 1 && this.state.checked.indexOf(id) === -1
    );
  };

  render() {
    const { data } = this.state;
    if (this.props.currentStep !== 4) {
      return null;
    }
    return (
      <div>
        <label>
          Please select your preference of stocks to invest in: (Select up to 4
          stocks in total from the displayed choices)
        </label>
        <Checkbox.Group style={{ width: "100%" }} onChange={this.onChange}>
          <Row>
            <Col span={8}>
              <Checkbox value="A" disabled={this.isDisabled("A")}>
                A
              </Checkbox>
            </Col>
            <Col span={8}>
              <Checkbox value="B" disabled={this.isDisabled("B")}>
                B
              </Checkbox>
            </Col>
            <Col span={8}>
              <Checkbox value="C" disabled={this.isDisabled("C")}>
                C
              </Checkbox>
            </Col>
            <Col span={8}>
              <Checkbox value="D" disabled={this.isDisabled("D")}>
                D
              </Checkbox>
            </Col>
            <Col span={8}>
              <Checkbox value="E" disabled={this.isDisabled("E")}>
                E
              </Checkbox>
            </Col>
          </Row>
        </Checkbox.Group>
      </div>
    );
  }
}

export default step5;
