import React, { Component } from "react";
import { Radio, Input } from "antd";
import { Select } from "antd";
const { Option } = Select;
class step5 extends Component {
  state = {
    data: [],
    ans: [],
    isMax: false,
  };
  componentDidMount() {}

  checkMax(event) {
    if (this.state.ans.length >= 5) {
      this.state.isMax = true;
      return;
    } else {
      let ans = this.state.ans;
      ans.push(event);
      this.state.ans = ans;
      this.state.isMax = false;
      this.props.handleChange(event, 7);
    }
  }

  render() {
    const { data } = this.state;
    if (this.props.currentStep !== 4) {
      return null;
    }
    return (
      <div>
        <label>
          Please select your preference of stocks to invest in: (select up to 4
          stocks regardless of the industry)
        </label>
        <Select
          mode="multiple"
          style={{ width: "100%" }}
          onChange={(event) => this.checkMax(event)}
        >
          {data.map((item) => {
            return (
              <Option key={item} value={item}>
                {item}
              </Option>
            );
          })}
        </Select>
      </div>
    );
  }
}

export default step5;
